import './index.css';

const Card = ({ unit }) => {
  return (
    <>
      <div className="card">
        <div className="content-card">
          <h2>Nome:</h2>
          <span>{unit.name}</span>
          <h2>Descrição:</h2>
          <span>{unit.description}</span>
          <h3>Expansão: {unit.expansion}</h3>
          <h3>Era: {unit.age}</h3>
          <h3>Ataque: {unit.attack}</h3>
          <h3>Armadura: {unit.armor}</h3>
        </div>
      </div>
    </>
  );
};

export default Card;
