import { useState, useEffect } from 'react';
import axios from 'axios';
import './index.css';

const Card = () => {
  const [unidade, setUnidade] = useState(null);

  useEffect(() => {});

  if (unidade === null) {
    return <p>Buscando unidades...</p>;
  }

  return (
    <>
      {unidade.map((item) => (
        <div className="card">
          <h1>Card Info</h1>
          <div className="content-card">
            <h2>Nome: {item.name}</h2>
            <h2>Descrição:</h2>
            <p>Teste de descrição</p>
            <h3>Expansão:</h3>
            <h3>Era:</h3>
            <h3>Ataque:</h3>
            <h3>Armadura:</h3>
          </div>
        </div>
      ))}
    </>
  );
};

export default Card;
